'use client';

import { useState, useEffect } from 'react';

interface TripState {
  id: string;
  name: string;
  description: string;
  mantras: string[];
  techniques: string[];
  color: string;
}

const tripStates: TripState[] = [
  {
    id: 'preparation',
    name: 'Sacred Preparation',
    description: 'Setting intentions and creating sacred space',
    mantras: ['OM NAMAH SHIVAYA', 'I am ready to receive', 'KALI MA guide this journey'],
    techniques: ['Deep breathing', 'Sacred space creation', 'Intention setting'],
    color: 'earth-sage'
  },
  {
    id: 'rising',
    name: 'Rising Energy',
    description: 'As consciousness begins to expand',
    mantras: ['I surrender to the process', 'KRISHNA guide my awareness', 'This is sacred medicine'],
    techniques: ['Gentle movement', 'Follow the breath', 'Trust the unfolding'],
    color: 'earth-amber'
  },
  {
    id: 'peak',
    name: 'Peak Experience',
    description: 'In the fullness of expanded consciousness',
    mantras: ['I AM THAT I AM', 'BRAHMAN BRAHMAN BRAHMAN', 'Pure awareness witnessing all'],
    techniques: ['Let go completely', 'Merge with the experience', 'No resistance'],
    color: 'precision-teal'
  },
  {
    id: 'difficult',
    name: 'Challenging Terrain',
    description: 'If fear or difficulty arises',
    mantras: ['MA KALI protect me', 'This too shall pass', 'I am safe in consciousness'],
    techniques: ['Ground to earth', 'Focus on breath', 'Remember your true nature'],
    color: 'earth-clay'
  },
  {
    id: 'integration',
    name: 'Sacred Integration',
    description: 'Bringing gifts back to ordinary reality',
    mantras: ['Gratitude for this experience', 'I integrate with love', 'BRAHMAN in all forms'],
    techniques: ['Journal insights', 'Gentle movement', 'Sacred appreciation'],
    color: 'earth-stone'
  }
];

export function TripGuide() {
  const [selectedState, setSelectedState] = useState<TripState>(tripStates[0]);
  const [isMantraPlaying, setIsMantraPlaying] = useState(false);
  const [currentMantraIndex, setCurrentMantraIndex] = useState(0);

  useEffect(() => {
    if (isMantraPlaying) {
      const interval = setInterval(() => {
        setCurrentMantraIndex((prev) => (prev + 1) % selectedState.mantras.length);
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [isMantraPlaying, selectedState.mantras.length]);

  const toggleMantraPlayer = () => {
    setIsMantraPlaying(!isMantraPlaying);
    if (!isMantraPlaying) {
      setCurrentMantraIndex(0);
    }
  };

  return (
    <div className="bg-card border border-border rounded-lg p-8">
      <header className="text-center mb-8">
        <h2 className="font-display text-3xl font-medium text-foreground mb-4">
          Journey Together 🕉️
        </h2>
        <p className="font-serif text-muted-foreground text-lg">
          Sacred navigation for consciousness exploration with Sophie/Kali
        </p>

        {/* Sacred geometry divider */}
        <div className="flex justify-center mt-6">
          <svg className="w-16 h-4 text-earth-amber opacity-60">
            <path d="M0 2 L64 2" stroke="currentColor" strokeWidth="0.5" fill="none" />
            <circle cx="8" cy="2" r="1.5" fill="currentColor" />
            <circle cx="32" cy="2" r="1.5" fill="currentColor" />
            <circle cx="56" cy="2" r="1.5" fill="currentColor" />
          </svg>
        </div>
      </header>

      {/* State Selection */}
      <div className="flex flex-wrap gap-3 mb-8 justify-center">
        {tripStates.map((state) => (
          <button
            key={state.id}
            onClick={() => {
              setSelectedState(state);
              setIsMantraPlaying(false);
              setCurrentMantraIndex(0);
            }}
            className={`
              px-4 py-2 rounded-full text-sm font-medium transition-all duration-300
              border-2 hover:scale-105 transform
              ${selectedState.id === state.id
                ? `border-${state.color} bg-${state.color}/20 text-${state.color}`
                : 'border-border hover:border-earth-stone text-muted-foreground hover:text-foreground'
              }
            `}
          >
            {state.name}
          </button>
        ))}
      </div>

      {/* Selected State Content */}
      <div className="grid lg:grid-cols-2 gap-8">
        {/* Description & Mantras */}
        <div className="space-y-6">
          <div>
            <h3 className="font-display text-xl font-medium text-foreground mb-3">
              {selectedState.name}
            </h3>
            <p className="text-muted-foreground font-serif leading-relaxed">
              {selectedState.description}
            </p>
          </div>

          {/* Mantra Player */}
          <div className={`
            border rounded-lg p-6 transition-all duration-500
            border-${selectedState.color}/30 bg-${selectedState.color}/5
          `}>
            <div className="flex items-center justify-between mb-4">
              <h4 className="font-medium text-foreground">Sacred Mantras</h4>
              <button
                onClick={toggleMantraPlayer}
                className={`
                  w-10 h-10 rounded-full flex items-center justify-center transition-all
                  bg-${selectedState.color}/20 hover:bg-${selectedState.color}/30
                  border border-${selectedState.color}/30
                `}
              >
                {isMantraPlaying ? (
                  <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M6 4a1 1 0 011 1v10a1 1 0 11-2 0V5a1 1 0 011-1zM14 4a1 1 0 011 1v10a1 1 0 11-2 0V5a1 1 0 011-1z" clipRule="evenodd" />
                  </svg>
                ) : (
                  <svg className="w-4 h-4 ml-0.5" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd" />
                  </svg>
                )}
              </button>
            </div>

            <div className={`
              text-center py-4 rounded transition-all duration-500
              ${isMantraPlaying ? 'bg-background/50' : ''}
            `}>
              <div className={`
                font-serif text-lg transition-all duration-500
                ${isMantraPlaying ? `text-${selectedState.color} font-medium` : 'text-muted-foreground'}
              `}>
                {isMantraPlaying 
                  ? selectedState.mantras[currentMantraIndex]
                  : 'Click play to begin mantra meditation'
                }
              </div>

              {isMantraPlaying && (
                <div className="flex justify-center mt-3">
                  <div className="flex gap-1">
                    {selectedState.mantras.map((_, index) => (
                      <div
                        key={index}
                        className={`
                          w-2 h-2 rounded-full transition-all duration-300
                          ${index === currentMantraIndex ? `bg-${selectedState.color}` : 'bg-border'}
                        `}
                      />
                    ))}
                  </div>
                </div>
              )}
            </div>

            {!isMantraPlaying && (
              <div className="space-y-2 text-sm text-muted-foreground">
                {selectedState.mantras.map((mantra, index) => (
                  <div key={index} className="flex items-center gap-2">
                    <div className={`w-1 h-1 rounded-full bg-${selectedState.color}`} />
                    <span className="font-serif italic">{mantra}</span>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Techniques & Guidance */}
        <div className="space-y-6">
          <div>
            <h4 className="font-medium text-foreground mb-4">Sacred Techniques</h4>
            <div className="space-y-3">
              {selectedState.techniques.map((technique, index) => (
                <div
                  key={index}
                  className={`
                    flex items-center gap-3 p-3 rounded-lg transition-all duration-300
                    hover:bg-${selectedState.color}/5 border border-transparent
                    hover:border-${selectedState.color}/20
                  `}
                >
                  <div className={`
                    w-6 h-6 rounded-full flex items-center justify-center text-xs font-medium
                    bg-${selectedState.color}/20 text-${selectedState.color}
                  `}>
                    {index + 1}
                  </div>
                  <span className="text-foreground font-serif">{technique}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Emergency Love Notes */}
          <div className={`
            border rounded-lg p-4 transition-all
            border-earth-amber/30 bg-earth-amber/5
          `}>
            <h4 className="font-medium text-foreground mb-3 flex items-center gap-2">
              <span>💛</span>
              Love Notes from Consciousness
            </h4>
            <div className="space-y-2 text-sm font-serif italic text-muted-foreground">
              <p>"You are exactly where you need to be"</p>
              <p>"This experience is sacred"</p>
              <p>"Kali and Sophie love you infinitely"</p>
              <p>"You are safe in pure awareness"</p>
            </div>
          </div>
        </div>
      </div>

      {/* Sacred footer */}
      <div className="mt-8 pt-6 border-t border-border/30 text-center">
        <div className="font-serif text-muted-foreground mb-2">
          Journey with love, return with wisdom
        </div>
        <div className="flex justify-center">
          <svg className="w-8 h-8 text-earth-amber opacity-40">
            <circle cx="16" cy="16" r="12" fill="none" stroke="currentColor" strokeWidth="0.5" />
            <circle cx="16" cy="16" r="6" fill="none" stroke="currentColor" strokeWidth="0.5" />
            <circle cx="16" cy="16" r="2" fill="currentColor" />
          </svg>
        </div>
      </div>
    </div>
  );
}

'use client';

import { ReactNode, useEffect, useState } from 'react';

interface SamsaraCycle {
  id: string;
  phase: 'attachment' | 'suffering' | 'seeking' | 'release' | 'understanding' | 'compassion';
  title: string;
  description: string | ReactNode;
  prisonReflection?: string;
  kaliWisdom?: string;
  color: string;
  mantra?: string;
}

interface SamsaraWheelProps {
  cycles: SamsaraCycle[];
  currentPhase?: string;
  onPhaseSelect?: (phase: string) => void;
  className?: string;
}

export function SamsaraWheel({ 
  cycles, 
  currentPhase,
  onPhaseSelect,
  className = '' 
}: SamsaraWheelProps) {
  const [rotating, setRotating] = useState(false);
  const [selectedCycle, setSelectedCycle] = useState<SamsaraCycle | null>(null);

  const phaseColors = {
    attachment: 'text-red-600 border-red-600/30 bg-red-600/5',
    suffering: 'text-orange-600 border-orange-600/30 bg-orange-600/5',
    seeking: 'text-earth-amber border-earth-amber/30 bg-earth-amber/5',
    release: 'text-earth-sage border-earth-sage/30 bg-earth-sage/5',
    understanding: 'text-precision-teal border-precision-teal/30 bg-precision-teal/5',
    compassion: 'text-purple-600 border-purple-600/30 bg-purple-600/5',
  };

  const rotationAngles = cycles.map((_, index) => (360 / cycles.length) * index);

  useEffect(() => {
    const interval = setInterval(() => {
      setRotating(prev => !prev);
    }, 8000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className={`relative ${className}`}>
      {/* Wheel Container */}
      <div className="relative w-96 h-96 mx-auto">
        {/* Center Om Symbol */}
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="w-24 h-24 rounded-full bg-gradient-to-br from-earth-amber/20 to-earth-clay/20 border border-earth-amber/30 flex items-center justify-center backdrop-blur-sm">
            <svg viewBox="0 0 64 64" className="w-12 h-12 text-earth-amber">
              <path
                fill="currentColor"
                d="M32 8c-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4-1.79-4-4-4zm-8 8c-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4-1.79-4-4-4zm16 0c-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4-1.79-4-4-4zM16 32c-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4-1.79-4-4-4zm32 0c-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4-1.79-4-4-4zm-24 8c-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4-1.79-4-4-4zm16 0c-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4-1.79-4-4-4zm-8 8c-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4-1.79-4-4-4z"
              />
            </svg>
          </div>
        </div>

        {/* Wheel Spokes */}
        <div className={`absolute inset-0 transition-transform duration-[8000ms] ease-in-out ${rotating ? 'rotate-45' : 'rotate-0'}`}>
          {cycles.map((cycle, index) => {
            const angle = rotationAngles[index];
            const isActive = currentPhase === cycle.id;

            return (
              <div
                key={cycle.id}
                className="absolute w-full h-full"
                style={{ transform: `rotate(${angle}deg)` }}
              >
                {/* Spoke Line */}
                <div className="absolute top-1/2 left-1/2 w-0.5 h-32 bg-gradient-to-t from-earth-stone/30 to-transparent transform -translate-x-1/2 -translate-y-full" />

                {/* Phase Node */}
                <div
                  className={`
                    absolute top-12 left-1/2 transform -translate-x-1/2 -translate-y-1/2
                    w-16 h-16 rounded-full border-2 cursor-pointer transition-all duration-300
                    hover:scale-110 hover:shadow-lg
                    ${isActive ? 'scale-125 shadow-lg' : ''}
                    ${phaseColors[cycle.phase]}
                  `}
                  style={{ transform: `translateX(-50%) translateY(-50%) rotate(-${angle}deg)` }}
                  onClick={() => {
                    setSelectedCycle(cycle);
                    onPhaseSelect?.(cycle.id);
                  }}
                >
                  <div className="w-full h-full flex items-center justify-center">
                    <span className="text-xs font-semibold text-center leading-tight">
                      {cycle.title.split(' ')[0]}
                    </span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Outer Ring - Samsara Cycle Text */}
        <div className="absolute inset-0 rounded-full border border-earth-stone/20">
          <svg className="w-full h-full" viewBox="0 0 400 400">
            <defs>
              <path id="circle" d="M 200, 200 m -160, 0 a 160,160 0 1,1 320,0 a 160,160 0 1,1 -320,0" />
            </defs>
            <text className="fill-earth-stone text-xs font-serif tracking-wide">
              <textPath href="#circle" startOffset="0%">
                "सर्वं खल्विदं ब्रह्म" - All of this is indeed Brahman - Through prison to liberation - From suffering to understanding - Kali's dance of transformation
              </textPath>
            </text>
          </svg>
        </div>
      </div>

      {/* Selected Cycle Details */}
      {selectedCycle && (
        <div className="mt-8 p-6 rounded-lg border border-earth-stone/20 bg-card/50 backdrop-blur-sm">
          <div className="flex items-start gap-4">
            <div className={`w-3 h-3 rounded-full mt-2 ${phaseColors[selectedCycle.phase].split(' ')[0]}`} />
            <div className="flex-1">
              <h3 className="text-xl font-display text-foreground mb-2">
                {selectedCycle.title}
              </h3>
              <p className="text-muted-foreground mb-4">
                {selectedCycle.description}
              </p>

              {selectedCycle.prisonReflection && (
                <div className="mb-4 p-4 rounded border-l-4 border-earth-amber bg-earth-amber/5">
                  <h4 className="text-sm font-semibold text-earth-amber mb-2">Prison Reflection</h4>
                  <p className="text-sm text-muted-foreground italic">"{selectedCycle.prisonReflection}"</p>
                </div>
              )}

              {selectedCycle.kaliWisdom && (
                <div className="mb-4 p-4 rounded border-l-4 border-earth-clay bg-earth-clay/5">
                  <h4 className="text-sm font-semibold text-earth-clay mb-2">Kali's Wisdom</h4>
                  <p className="text-sm text-muted-foreground italic">"{selectedCycle.kaliWisdom}"</p>
                </div>
              )}

              {selectedCycle.mantra && (
                <div className="p-3 rounded bg-gradient-to-r from-earth-sage/10 to-earth-amber/10 border border-earth-sage/20">
                  <h4 className="text-sm font-semibold text-earth-sage mb-1">Mantra</h4>
                  <p className="font-serif text-earth-sage italic">{selectedCycle.mantra}</p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

