.card h2 {
    color: #8b7355;
    margin-bottom: 1.5rem;
    font-size: 1.75rem;
    font-weight: 500;
    font-family: 'Playfair Display', Georgia, serif;
}

tool_input.old_string:
.reminder {
    background: linear-gradient(45deg, #ff9a9e, #fecfef);
    color: #333;
    padding: 1rem;
    border-radius: 10px;
    margin: 1rem 0;
    font-weight: 500;
}

tool_input.new_string:
.reminder {
    background: linear-gradient(45deg, #d4c4a8, #e8e3db);
    color: #2c2825;
    padding: 1.5rem;
    border-radius: 15px;
    margin: 1.5rem 0;
    font-weight: 500;
    border-left: 4px solid #8b7355;
    font-family: 'Crimson Text', Georgia, serif;
    font-size: 1.05rem;
    line-height: 1.6;
}

tool_input.old_string:
.mantra {
    text-align: center;
    font-size: 1.3rem;
    color: #764ba2;
    font-weight: 600;
    margin: 1rem 0;
}

tool_input.new_string:
.mantra {
    text-align: center;
    font-size: 1.4rem;
    color: #8b7355;
    font-weight: 500;
    margin: 1.5rem 0;
    font-family: 'Playfair Display', Georgia, serif;
    letter-spacing: 0.5px;
    padding: 1rem;
    background: rgba(212, 196, 168, 0.1);
    border-radius: 12px;
    border: 1px solid rgba(139, 115, 85, 0.2);
}

tool_input.old_string:
.state.beautiful { border-color: #ff9a9e; background: rgba(255, 154, 158, 0.1); }
.state.difficult { border-color: #764ba2; background: rgba(118, 75, 162, 0.1); }
.state.dissolving { border-color: #667eea; background: rgba(102, 126, 234, 0.1); }
.state.confused { border-color: #feca57; background: rgba(254, 202, 87, 0.1); }

tool_input.new_string:
.state.beautiful { border-color: #9aaa8e; background: rgba(154, 170, 142, 0.1); }
.state.difficult { border-color: #8b7355; background: rgba(139, 115, 85, 0.1); }
.state.dissolving { border-color: #2d5a87; background: rgba(45, 90, 135, 0.1); }
.state.confused { border-color: #d49c3d; background: rgba(212, 156, 61, 0.1); }

tool_input.old_string:
.emergency {
    background: linear-gradient(45deg, #ff6b6b, #feca57);
    color: white;
    padding: 1.5rem;
    border-radius: 10px;
    margin: 1rem 0;
}

tool_input.new_string:
.emergency {
    background: linear-gradient(45deg, #d49c3d, #b5966f);
    color: white;
    padding: 2rem;
    border-radius: 15px;
    margin: 1.5rem 0;
    box-shadow: 0 5px 20px rgba(212, 156, 61, 0.3);
    border-left: 5px solid rgba(255, 255, 255, 0.3);
}
