@import "tailwindcss";

:root {
  /* Sacred Minimalism - Light Mode */
  --background: #faf9f7;
  --foreground: #2c2825;
  --card: #ffffff;
  --card-foreground: #2c2825;
  --primary: #8b7355;
  --primary-foreground: #faf9f7;
  --secondary: #e8e3db;
  --secondary-foreground: #2c2825;
  --accent: #d4c4a8;
  --accent-foreground: #2c2825;
  --muted: #f1ede6;
  --muted-foreground: #6b6358;
  --border: #e5dfd5;
  --input: #e5dfd5;
  --ring: #8b7355;

  /* Sacred earth tones */
  --earth-stone: #d4c4a8;
  --earth-clay: #b5966f;
  --earth-amber: #d49c3d;
  --earth-moss: #7d8471;
  --earth-sage: #9aaa8e;

  /* Gallifrey-inspired precision accents */
  --precision-teal: #2d5a87;
  --precision-charcoal: #3a3a3a;
  --precision-light: #f8f9fa;
}

@theme inline {
  /* Colors */
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);

  /* Sacred earth palette */
  --color-earth-stone: var(--earth-stone);
  --color-earth-clay: var(--earth-clay);
  --color-earth-amber: var(--earth-amber);
  --color-earth-moss: var(--earth-moss);
  --color-earth-sage: var(--earth-sage);

  /* Precision accents */
  --color-precision-teal: var(--precision-teal);
  --color-precision-charcoal: var(--precision-charcoal);
  --color-precision-light: var(--precision-light);

  /* Typography */
  --font-sans: Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
  --font-serif: "Crimson Text", "Georgia", serif;
  --font-display: "Playfair Display", "Georgia", serif;
  --font-mono: "JetBrains Mono", monospace;

  /* Spacing for contemplative design */
  --spacing-xs: 0.25rem;
  --spacing-sm: 0.5rem;
  --spacing-md: 1rem;
  --spacing-lg: 1.5rem;
  --spacing-xl: 2rem;
  --spacing-2xl: 3rem;
  --spacing-3xl: 4rem;

  /* Border radius for organic feel */
  --radius: 0.5rem;
  --radius-sm: 0.375rem;
  --radius-lg: 0.75rem;
  --radius-xl: 1rem;
}

@media (prefers-color-scheme: dark) {
  :root {
    /* Sacred Minimalism - Dark Mode (contemplative) */
    --background: #1a1714;
    --foreground: #f0ede8;
    --card: #2c2825;
    --card-foreground: #f0ede8;
    --primary: #d4c4a8;
    --primary-foreground: #1a1714;
    --secondary: #3a352f;
    --secondary-foreground: #f0ede8;
    --accent: #4a453e;
    --accent-foreground: #f0ede8;
    --muted: #2c2825;
    --muted-foreground: #a8a196;
    --border: #3a352f;
    --input: #3a352f;
    --ring: #d4c4a8;

    /* Darker earth tones */
    --earth-stone: #8b7b68;
    --earth-clay: #7d6b4f;
    --earth-amber: #b8892e;
    --earth-moss: #5c6350;
    --earth-sage: #6d7a63;

    /* Dark precision accents */
    --precision-teal: #4a7ba7;
    --precision-charcoal: #2a2a2a;
    --precision-light: #2c2825;
  }
}

/* Base styles with sacred geometry */
body {
  background: var(--background);
  color: var(--foreground);
  font-family: var(--font-sans);
  line-height: 1.7;
  font-feature-settings: "rlig" 1, "calt" 1;
}

/* Contemplative scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--muted);
}

::-webkit-scrollbar-thumb {
  background: var(--earth-clay);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--primary);
}

/* Sacred text selection */
::selection {
  background: var(--earth-amber);
  color: var(--background);
}

/* Smooth transitions for contemplative interactions */
* {
  transition: colors 150ms ease, border-color 150ms ease, background-color 150ms ease, 
              box-shadow 150ms ease, transform 150ms ease;
}

